// @flow
import React from 'react';
import { Constants } from '@data';

import type { MottoType } from '@data/Constants/mottos';
import { Motto } from '../Motto';

import styles from './Footer.module.css';

export type FooterProps = {
    motto?: MottoType,
};

export const Footer = ({ motto }: FooterProps) => {
    const build = Constants.config.BUILD_INFO;
    let buildInfo = '';

    if (build && Object.keys(build).length > 0) {
        buildInfo = ' | ';

        if (build.build) {
            buildInfo += `build-${build.build}-`;
        }

        if (build.git) {
            buildInfo += `${build.git} `;
        }
    }

    return (
        <footer className={`${styles.footer} footer`}>
            <div className="container-fluid">
                <div className={`${styles['footer-copyright']} footer-copyright`}>
                    {motto
                        ? (
                            <small className={styles['tt-motto']}>
                                <Motto house={motto.house} motto={motto.motto} />
                            </small>
                        )
                        : ''}
                    <i className="fa fa-copyright" />
                    <a href="http://25th-floor.com">25th-floor GmbH</a>
                    <span>{buildInfo}</span>
                </div>
            </div>
        </footer>
    );
};

export default Footer;
