// @flow
import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';

import type { MottoType } from '@data/Constants/mottos';

type MottoProps = {
    motto: MottoType,
    isAuthenticated: boolean,
};
/**
 * Motto
 */
export const Motto = ({ motto, isAuthenticated }: MottoProps) => {
    if (!isAuthenticated) return null;
    return (<span title={motto.house}>{motto.motto}</span>);
};

const mapStateToProps = ({ auth, motto }) => ({
    motto: motto.motto,
    isAuthenticated: auth.isAuthenticated,
});

const mapDispatchToProps = dispatch => bindActionCreators({
}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Motto);
