// @flow
import React from 'react';
import { connect } from 'react-redux';

import type { MottoType } from '@data/Motto';

type MottoProps = {
    motto: MottoType,
};
/**
 * Motto
 */
export const Motto = ({ motto }: MottoProps) => (<span title={motto.house}>{motto.motto}</span>);

const mapStateToProps = ({ motto }) => ({
    motto: motto.motto,
});

export default connect(mapStateToProps)(Motto);
