// @flow
import React from 'react';
import { connect } from 'react-redux';

import type { MottoType } from '@data/Motto';

type MottoProps = {
    motto: MottoType,
    isAuthenticated: boolean,
};
/**
 * Motto
 */
export const Motto = ({ motto, isAuthenticated }: MottoProps) => {
    if (!isAuthenticated) return null;
    return (<span title={motto.house}>{motto.motto}</span>);
};

const mapStateToProps = ({ auth, motto }) => ({
    motto: motto.motto,
    isAuthenticated: auth.isAuthenticated,
});

export default connect(mapStateToProps)(Motto);
