import R from 'ramda';
import React from 'react';
import { Constants } from '../../@data';

// import './Motto.module.css';

const { mottos } = Constants;

/**
 * return a random number in range of min max
 * @param {integer} rmin
 * @param {integer} rmax
 */
function getRandomInt(rmin, rmax) {
    const c = Math.ceil(rmin);
    const f = Math.floor(rmax);
    return Math.floor(Math.random() * (f - c)) + c;
}

const max = R.length(mottos);
const random = getRandomInt(1, max - 1);
const motto = mottos[random];

/**
 * Motto
 */
export const Motto = () => (<span title={motto.house}>{motto.motto}</span>);

export default Motto;
