import React from 'react';

import { Constants } from '@data';

const getVersionString = () => {
    const data = [];

    const version = Constants.config.APP_VERSION;
    if (version) {
        data.push(`build-${version}`);
    }

    const git = Constants.config.APP_GIT;
    if (git) {
        data.push(git);
    }

    return data.join('-');
};

/**
 * Version
 */
export const Version = () => (<span>{getVersionString()}</span>);

export default Version;
