// @flow

import actionTypes from './AuthTypes';

import type { UserType } from '../';
import type { LoginActionType, LogoutActionType } from './AuthTypes';

export function login(user: UserType): LoginActionType {
    return {
        type: actionTypes.LOGIN,
        user,
    };
}

export function logout(user: UserType): LogoutActionType {
    return {
        type: actionTypes.LOGOUT,
        user,
    };
}

export default {
    login,
    logout,
};
