// @flow

import type { ApiUserType } from '@data/Resources/ResourcesTypes';
import actionTypes, {
    type LoginActionType,
    type LogoutActionType,
} from './AuthTypes';

export function login(user: ApiUserType): LoginActionType {
    return {
        type: actionTypes.LOGIN,
        user,
    };
}

export function logout(user: ApiUserType): LogoutActionType {
    return {
        type: actionTypes.LOGOUT,
        user,
    };
}

export default {
    login,
    logout,
};
