import moment from 'moment';

const DATE_FORMAT = 'YYYY-MM-DD';
const today = moment().format('YYYY-MM-DD');

function getRandomInt(min, max) {
    const c = Math.ceil(min);
    const f = Math.floor(max);
    return Math.floor(Math.random() * (f - c)) + c;
}

export const utils = {
    today,
    now: today,
    getRandomInt,
};
