import actionTypes from './AuthActionTypes';

const initialState = {
    user: {},
    isAuthenticated: false,
};

export const AuthReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.LOGIN: {
            const { user } = action;
            return {
                ...state,
                user,
                isAuthenticated: true,
            };
        }

        case actionTypes.LOGOUT: {
            return {
                ...state,
                user: {},
                isAuthenticated: false,
            };
        }

        default:
            return state;
    }
};
